using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Diagnostics;
using System.Drawing;
using System.Linq;
using System.Reflection;
using System.Text;
using System.Threading.Tasks;
using System.Windows.Forms;

namespace Abdal_Admin_Finder
{
    public partial class Legal_disclaimer : Telerik.WinControls.UI.RadForm
    {
        private string abdal_app_name_Legal_disclaimer =
            Assembly.GetExecutingAssembly().GetName().ToString().Split(',')[0];
        public Legal_disclaimer()
        {
            InitializeComponent();
        }

        private void Legal_disclaimer_Load(object sender, EventArgs e)
        {
            // Call Global Chilkat Unlock
            Abdal_Security_Group_App.GlobalUnlockChilkat GlobalUnlock =
                new Abdal_Security_Group_App.GlobalUnlockChilkat();
            GlobalUnlock.unlock();


            DateTime get_current_year = DateTime.Now;

            string part_one_b64 =
                "";
            Chilkat.BinData bd2 = new Chilkat.BinData();
            bd2.AppendEncoded(part_one_b64, "base64");
            string decoded = bd2.GetString("utf-8");
            string part_one_b64_to_text = decoded;
            bd2.Clear();

            string part_two_b64 =
                "";
            bd2.AppendEncoded(part_two_b64, "base64");
            decoded = bd2.GetString("utf-8");
            string part_two_b64_to_text = decoded;
            bd2.Clear();


            richTextBox1.Text = "Disclaimer" + Environment.NewLine + "Last updated: December 01, " +
                                get_current_year.Year.ToString() + Environment.NewLine +
                                part_one_b64_to_text + " " + abdal_app_name_Legal_disclaimer + Environment.NewLine +
                                part_two_b64_to_text;
        }

        private void radButton2_Click(object sender, EventArgs e)
        {
            Process.GetCurrentProcess().Kill();
            Environment.Exit(0);
        }

        private void radButton1_Click(object sender, EventArgs e)
        {
            this.Hide();
            Abdal_Security_Group_App.Abdal_Admin_Finder main_form = new Abdal_Security_Group_App.Abdal_Admin_Finder();
            main_form.Show();
        }
    }
}
